mapper.select:
SELECT B.* FROM ( SELECT ROWNUM RNUM, A.* FROM ( SELECT br.bg_r_id, br.bg_r_use_ts, br.bg_r_amt, br.bg_r_rsn, br.bg_r_status, br.bg_r_ts, br.bg_r_res_rsn, br.bg_r_res_ts, emp.emp_nm AS "empEmpNm", sal.emp_nm AS "salEmpNm", cat.aca_nm AS "acaNm" FROM BUDGET_REQ br LEFT JOIN employee emp ON br.emp_emp_no = emp.emp_no LEFT JOIN employee sal ON br.sal_emp_no = sal.emp_no LEFT JOIN account_category cat ON br.aca_id = cat.aca_id
ORDER BY bg_r_ts DESC ) A ) B
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.insert:

	SELECT 'BG_REQ_' || LPAD(BUDGET_REQ_SEQ.NEXTVAL, 4, '0') AS bgRId FROM DUAL

