mapper.select:
SELECT B.*, E.EMP_NM FROM ( SELECT ROWNUM RNUM, A.* FROM ( SELECT EB_NO, EB_TITLE, EMP_NO, EB_CRT_DT, EB_VWS FROM EMP_BRD
ORDER BY EB_NO DESC ) A ) B JOIN EMPLOYEE E ON B.EMP_NO = E.EMP_NO
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.insert:

   SELECT EMP_BRD_SEQ.NEXTVAL
