mapper.update:

UPDATE EMP_BRD
SET
   EB_NO = #{ebNo, jdbcType=NUMERIC}
   , EB_TITLE = #{ebTitle, jdbcType=VARCHAR}
   , EB_ATCH_FILE = #{atchFileId,jdbcType=CHAR}
   , EB_PUB = #{ebPub, jdbcType=CHAR}
   , EB_UPD_DT = SYSDATE

WHERE EB_NO = #{ebNo}
