INSERT INTO RNC_REQ (
RR_ID,
RR_LOC,
RR_CONTENT,
RR_IMG,
RR_OCCUR_TS,
RR_TS,
RR_RS_ID,
RR_ACC,
RR_STTS,
RR_TYPE
) VALUES (
#{rrId,jdbcType=VARCHAR}
, #{rrLoc,jdbcType=VARCHAR}
,
#{rrContent,jdbcType=VARCHAR}
, #{rrImg,jdbcType=VARCHAR}
, #{rrOccurTs,
jdbcType=TIMESTAMP}
, SYSTIMESTAMP
, #{rrRsId,jdbcType=VARCHAR}
,
#{rrAcc,jdbcType=CHAR}
,'UN'
,
#{rrType,jdbcType=VARCHAR}
)

