SELECT B.* FROM ( SELECT ROWNUM RNUM, A.* FROM ( SELECT RR_ID, ZONE_FLOOR "zone.zoneFloor", ZONE_CTGR "zone.zoneCtgr", RR_CONTENT, RR_TS, EMP_NM "employee.empNm", CODE_NM "common.codeNm", RR_STTS, RR_TYPE FROM RNC_REQ INNER JOIN ZONE ON (RR_LOC=ZONE_CD) LEFT JOIN EMPLOYEE ON (RR_ACC = EMP_NO) LEFT JOIN COMMON_CODE ON (RR_STTS = CODE_ID) WHERE RR_TYPE = 'REF'
ORDER BY RR_OCCUR_TS DESC ) A ) B
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

